import { useState } from "react";

export default App;

function App() {
  const [items, setItem] = useState([]);
  // const [showlist, setShowList] = useState(false);

  function HandleAddItems(item) {
    setItem((items) => [...items, item]);
    // console.log(item);
  }

  return (
    <div className="app">
      <Header />
      <Form onAddItems={HandleAddItems} />
      <PackagingList items={items} />
      <Footer />
    </div>
  );
}

function Header() {
  return (
    <div>
      <h1>Let's Go To Travel</h1>
    </div>
  );
}

function Form({ onAddItems }) {
  const [itemno, setItemNo] = useState(1);
  const [itemName, setItemname] = useState("");

  function HandleSubmit(e) {
    e.preventDefault();
    if (!itemName) return;

    const newdata = {
      description: itemName,
      quantity: itemno,
      id: Date.now(),
      packed: false,
    };

    console.log(newdata);
    onAddItems(newdata);

    setItemNo(1);
    setItemname("");
  }

  return (
    <form className="add-form" onSubmit={HandleSubmit}>
      <h3>What does you need for your trip?</h3>

      <select
        value={itemno}
        onChange={(e) => setItemNo(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map(
          (
            num //This is for creating the select type option range.
          ) => (
            <option value={Number(num)} key={num}>
              {num}
            </option>
          )
        )}
      </select>

      <input
        type="text"
        value={itemName}
        onChange={(e) => {
          setItemname(e.target.value);
        }}
      />

      <button onClick={HandleSubmit}>Add</button>
    </form>
  );
}

function PackagingList({ items }) {
  return (
    <div className="list">
      <ul>
        {items.map((item) => (
          <Item item={item} key={item.id} />
        ))}
      </ul>
    </div>
  );
}

function Item({ item }) {
  // console.log(desciption.desciption + " " + quantit;
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity}
        {item.description}
      </span>
      <button>‚ùå</button>
    </li>
  );
}

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: true },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Socks", quantity: 12, packed: false },
  { id: 4, description: "Socks", quantity: 12, packed: false },
  { id: 5, description: "Socks", quantity: 12, packed: true },
];

function Footer() {
  return (
    <footer className="stats">
      <em>
        You have packed x items on your list , and you already packed x (x %)
      </em>
    </footer>
  );
}
