import { useState } from "react";

export default App;

function App() {
  return (
    <div className="app">
      <Header />
      <Form />
      <PackagingList />
      <Footer />
    </div>
  );
}

function Header() {
  return (
    <div>
      <h1>Let's Go To Travel</h1>
    </div>
  );
}

function Form() {
  const [itemno, setItemNo] = useState(1);
  const [itemName, setItemname] = useState("");
  const [showlist, setShowList] = useState(false);

  const options = [];
  for (let i = 1; i <= 100; i++) {
    options.push(
      <option key={i} value={i}>
        {i}
      </option>
    );
  }

  function GetItemNo(e) {
    console.log(e.target.value);
    setItemNo(e.target.value);
    // setItemNo();
  }

  function GetItemInput(e) {
    setItemname(e.target.value);
    console.log(e.target.value);
  }

  function AddItem() {
    setShowList(true);
    console.log(itemno + " " + itemName);
  }

  return (
    <div className="add-form">
      <h3>What does you need for your trip?</h3>
      <select onChange={GetItemNo}>{options}</select>

      <input type="text" onInput={GetItemInput} />
      <button onClick={AddItem}>Add</button>
      {/* {showlist && <PackagingList ItemNo={itemno} itemName={itemName} />} */}
    </div>
  );
}

function PackagingList(props) {
  // console.log(props);
  return (
    <div className="list">
      <ul key={initialItems}>
        {initialItems.map((item) => (
          <Item item={item} />
        ))}
      </ul>
    </div>
  );
}

function Item(item) {
  // console.log(desciption.desciption + " " + quantit;
  return <li>{item.description}</li>;
}

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
];

function Footer() {
  return (
    <footer className="stats">
      <em>
        You have packed x items on your list , and you already packed x (x %)
      </em>
    </footer>
  );
}
