import { useState } from "react";

export default App;

function App() {
  return (
    <div className="app">
      <Header />
      <Form />
      <PackagingList />
      <Footer />
    </div>
  );
}

function Header() {
  return (
    <div>
      <h1>Let's Go To Travel</h1>
    </div>
  );
}

function Form() {
  const [itemno, setItemNo] = useState(1);
  const [itemName, setItemname] = useState("");
  const [showlist, setShowList] = useState(false);

  // function GetItemNo(e) {
  //   console.log(e.target.value);
  //   setItemNo(e.target.value);
  //   // setItemNo();
  // }

  // function GetItemInput(e) {
  //   setItemname(e.target.value);
  //   console.log(e.target.value);
  // }

  function AddItem() {
    setShowList(true);
    console.log(itemno + " " + itemName);
  }

  function HandleSubmit(e) {
    e.preventDefault();
  }

  return (
    <form className="add-form" onSubmit={HandleSubmit}>
      <h3>What does you need for your trip?</h3>
      <select
        value={itemno}
        onChange={(e) => Number(setItemNo(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map(
          (
            num //This is for creating the select type option range.
          ) => (
            <option value={num} key={num}>
              {num}
            </option>
          )
        )}
      </select>

      <input
        type="text"
        onChange={(e) => {
          console.log(e.target.value);
          setItemname(e.target.value);
        }}
      />
      <button onClick={AddItem}>Add</button>
      {/* {showlist && <PackagingList ItemNo={itemno} itemName={itemName} />} */}
    </form>
  );
}

function PackagingList(props) {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item item={item} key={item.id} />
        ))}
      </ul>
    </div>
  );
}

function Item({ item }) {
  // console.log(desciption.desciption + " " + quantit;
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity}
        {item.description}
      </span>
      <button>‚ùå</button>
    </li>
  );
}

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: true },
  { id: 2, description: "Socks", quantity: 12, packed: false },
];

function Footer() {
  return (
    <footer className="stats">
      <em>
        You have packed x items on your list , and you already packed x (x %)
      </em>
    </footer>
  );
}
