import { useState } from "react";

export default App;

function App() {
  const [items, setItem] = useState([]);
  // const [showlist, setShowList] = useState(false);

  function HandleAddItems(item) {
    setItem((items) => [...items, item]);
    // console.log(item);
  }

  function HandleDeleteItem(id) {
    setItem((items) => items.filter((item) => item.id != id));
  }

  function HandleToggleItem(id) {
    setItem((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }

  return (
    <div className="app">
      <Header />
      <Form onAddItems={HandleAddItems} />
      <PackagingList
        items={items}
        onDeleteItems={HandleDeleteItem}
        OnToggleItem={HandleToggleItem}
      />
      <Footer totalitems={items} />
    </div>
  );
}

function Header() {
  return (
    <div>
      <h1>Let's Go To Travel</h1>
    </div>
  );
}

function Form({ onAddItems }) {
  const [itemno, setItemNo] = useState(1);
  const [itemName, setItemname] = useState("");

  function HandleSubmit(e) {
    e.preventDefault();
    if (!itemName) return;

    const newdata = {
      description: itemName,
      quantity: itemno,
      id: Date.now(),
      packed: false,
    };

    console.log(newdata);
    onAddItems(newdata);

    setItemNo(1);
    setItemname("");
  }

  return (
    <form className="add-form" onSubmit={HandleSubmit}>
      <h3>What does you need for your trip?</h3>

      <select
        value={itemno}
        onChange={(e) => setItemNo(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map(
          (
            num //This is for creating the select type option range.
          ) => (
            <option value={Number(num)} key={num}>
              {num}
            </option>
          )
        )}
      </select>

      <input
        type="text"
        value={itemName}
        onChange={(e) => {
          setItemname(e.target.value);
        }}
      />

      <button onClick={HandleSubmit}>Add</button>
    </form>
  );
}

function PackagingList({ items, onDeleteItems, OnToggleItem }) {
  return (
    <div className="list">
      <ul>
        {items.map((item) => (
          <Item
            item={item}
            key={item.id}
            deleteItem={onDeleteItems}
            toggleitem={OnToggleItem}
          />
        ))}
      </ul>
    </div>
  );
}

function Item({ item, deleteItem, toggleitem }) {
  return (
    <li>
      <input type="checkbox" onChange={() => toggleitem(item.id)} />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity}
        {item.description}
      </span>
      <button onClick={() => deleteItem(item.id)}>‚ùå</button>
    </li>
  );
}

function Footer({ totalitems }) {
  const itemtotal = totalitems.length;
  const itempacked = totalitems.filter((item) => item.packed).length;

  return (
    <footer className="stats">
      <em>
        You have packed {itemtotal} items on your list , and you already packed
        {itempacked} (x %)
      </em>
    </footer>
  );
}
