import { useState } from "react";

export default App;

function App() {
  return (
    <div className="app">
      <Header />
      <Form />
      <PackagingList />
      <Footer />
    </div>
  );
}

function Header() {
  return (
    <div>
      <h1>Let's Go To Travel</h1>
    </div>
  );
}

function Form() {
  const [itemno, setItemNo] = useState(1);
  const [itemName, setItemname] = useState("");
  const [showlist, setShowList] = useState(false);

  const options = [];
  for (let i = 1; i <= 100; i++) {
    options.push(
      <option key={i} value={i}>
        {i}
      </option>
    );
  }

  function GetItemNo(e) {
    console.log(e.target.value);
    setItemNo(e.target.value);
    // setItemNo();
  }

  function GetItemInput(e) {
    setItemname(e.target.value);
    console.log(e.target.value);
  }

  function AddItem() {
    setShowList(true);
    console.log(itemno + " " + itemName);
  }

  return (
    <div className="add-form">
      <span>
        <h3>What does you need for your trip?</h3>
      </span>
      <select onChange={GetItemNo}>{options}</select>

      <input type="text" onInput={GetItemInput} />
      {showlist && <PackagingList ItemNo={itemno} itemName={itemName} />}
      <button onClick={AddItem}>Add</button>
    </div>
  );
}

function PackagingList(props) {
  console.log(props);
  return (
    <div className="list">
      <ul>
        <li>
          <input type="checkbox" /> Paaport
        </li>
      </ul>
    </div>
  );
}

function Footer() {
  return (
    <footer className="stats">
      <em>
        You have packed x items on your list , and you already packed x (x %)
      </em>
    </footer>
  );
}
