import { useState } from "react";
import Header from "./header";
import Form from "./form";
import PackagingList from "./packaginglist";
export default App;

function App() {
  const [items, setItem] = useState([]);
  // const [showlist, setShowList] = useState(false);

  function HandleAddItems(item) {
    setItem((items) => [...items, item]);
    // console.log(item);
  }

  function HandleDeleteItem(id) {
    setItem((items) => items.filter((item) => item.id != id));
  }

  function HandleToggleItem(id) {
    setItem((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }

  return (
    <div className="app">
      <Header />
      <Form onAddItems={HandleAddItems} />
      <PackagingList
        items={items}
        onDeleteItems={HandleDeleteItem}
        OnToggleItem={HandleToggleItem}
        OnClearItem={setItem}
      />
      <Footer totalitems={items} />
    </div>
  );
}

function Item({ item, deleteItem, toggleitem }) {
  return (
    <li>
      <input type="checkbox" onChange={() => toggleitem(item.id)} />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={() => deleteItem(item.id)}>‚ùå</button>
    </li>
  );
}

function Footer({ totalitems }) {
  if (totalitems.length === 0) {
    return <p className="stats">Let Pack The Bag And Explore The world ‚úàÔ∏è</p>;
  }
  const itemtotal = totalitems.length;
  const itempacked = totalitems.filter((item) => item.packed).length;
  const packedpercentage = Math.round((itempacked / itemtotal) * 100);

  return (
    <footer className="stats">
      <em>
        {100 !== packedpercentage ? (
          <p>
            You have packed {itemtotal} items on your list , and you already
            packed {itempacked} ({packedpercentage}%){" "}
          </p>
        ) : (
          "Hey , you have got everything , now let's Go!!! üöÄ"
        )}
      </em>
    </footer>
  );
}
